!function(){$("#myChart").length&&$.getJSON("/stream",function(t){if(!t.options)return void $(".chart").prepend("<a href='/'><h3>Error loading chart.</h3></a>");var a=[],e=[];t.options.forEach(function(t){a.push(t.votes),e.push(t.name)});var o=document.getElementById("myChart").getContext("2d");new Chart(o,{type:"bar",data:{labels:e,datasets:[{data:a,backgroundColor:"#ff986d",label:"# of votes"}]}})})}();